export class Draft {
    base;
    head;
    tip;
    constructor(opts) {
        const base = (this.base = opts.base);
        const head = (this.head = base.clone());
        this.tip = opts.tip;
        for (const patch of opts.head)
            head.applyPatch(patch);
    }
    rebase(patches) {
        this.base.applyBatch(patches);
        this.head.applyBatch(patches);
    }
    advance(index) { }
    undo() { }
    redo() { }
}
