export class CliCodecRaw {
    id = 'raw';
    description = 'Raw data, useful for strings and binary data';
    encode(value) {
        if (value instanceof Uint8Array)
            return value;
        const str = String(value);
        return new TextEncoder().encode(str);
    }
    decode(bytes) {
        throw new Error('Not available');
    }
}
