export const defineBuiltinRoutes = (r) => {
    return r.extend((t, r) => [
        r('.echo', t.Function(t.any, t.any).options({
            title: 'Echo input',
            description: 'Echo the input value back to the caller',
        }), async (req) => req),
        r('.type', t.Function(t.undef, t.any).options({
            title: 'Type information',
            description: 'Returns whole type system of this CLI.',
        }), async (request, ctx) => {
            return ctx.cli.types.exportTypes();
        }),
    ]);
};
