export class CliParamVersion {
    param = 'version';
    short = 'v';
    title = 'Print version and exit';
    createInstance = (cli, pointer, value) => new (class {
        onParam = async () => {
            const version = cli.options.version ?? '0.0.0-unknown';
            cli.stdout.write(version + '\n');
            cli.exit(0);
        };
    })();
}
